/* ------------------------------------------------------------------
      Send hearts data to UI
------------------------------------------------------------------ */

figma.ui.onmessage = (msg) => {
  if (msg.type === "request-hearts") {
    figma.ui.postMessage({
      type: "load-hearts",
      hearts: hearts,
    });
  }

  /* ------------------------------------------------------------------
  Handle inserting hearts to Figma
  ------------------------------------------------------------------ */

  if (msg.type === "insert-heart") {
    const heartSVG = hearts[msg.heartType];

    /* ------------------------------------------------------------------
    Convert SVG to a Figma node
    -> SVGs <path> elements are translated into Figma VECTOR nodes,
    the vector are needed to colorize hearts.
    ------------------------------------------------------------------ */

    const node = figma.createNodeFromSvg(heartSVG);
    const heartVector = node.findOne((n) => n.type === "VECTOR") as VectorNode;

    if (heartVector) {
      figma.currentPage.appendChild(heartVector);
      // Remove annoying frame wrapper :)
      node.remove();

      /* ------------------------------------------------------------------
      Apply Gradient or Color
      -> The gradient is applied inside Figma after insertion, since Figma
      doesn’t seem to understand <linearGradient> from SVG.
      ------------------------------------------------------------------ */

      if (msg.color === "gradient") {
        const gradientFill: GradientPaint = {
          type: "GRADIENT_LINEAR",
          gradientStops: [
            {
              color: { r: 206 / 255, g: 88 / 255, b: 73 / 255, a: 1 },
              position: 0,
            },
            {
              color: { r: 227 / 255, g: 132 / 255, b: 67 / 255, a: 1 },
              position: 0.2,
            },
            {
              color: { r: 230 / 255, g: 193 / 255, b: 93 / 255, a: 1 },
              position: 0.4,
            },
            {
              color: { r: 90 / 255, g: 164 / 255, b: 101 / 255, a: 1 },
              position: 0.6,
            },
            {
              color: { r: 82 / 255, g: 120 / 255, b: 184 / 255, a: 1 },
              position: 0.8,
            },
            {
              color: { r: 102 / 255, g: 84 / 255, b: 158 / 255, a: 1 },
              position: 1,
            },
          ],
          gradientTransform: [
            [1, 0, 0],
            [0, 1, 0],
          ],
        };
        heartVector.fills = [gradientFill as Paint];
      } else {
        /* ------------------------------------------------------------------
      Convert HEX to RGB
      -> Figma’s API does not support HEX color codes directly.
      -> RGB values in Figma range from 0 to 1 (not 0-255).
      ------------------------------------------------------------------ */

        const colorHex = msg.color;
        const r = parseInt(colorHex.substring(1, 3), 16) / 255;
        const g = parseInt(colorHex.substring(3, 5), 16) / 255;
        const b = parseInt(colorHex.substring(5, 7), 16) / 255;

        const solidFill: SolidPaint = {
          type: "SOLID",
          color: { r, g, b },
        };
        heartVector.fills = [solidFill as Paint];
      }

      figma.viewport.scrollAndZoomIntoView([heartVector]);
    }

    figma.closePlugin();
  }
};
